import "primereact/resources/themes/lara-light-indigo/theme.css";
import "primereact/resources/primereact.css";

const HotelRoomCard = () => {
  const footer = (
    <>
      <Group>
        <Text weight={700} size="lg" style={{ position: "absolute" }}>
          $300
        </Text>
        <Text weight={400} size="sm" style={{ marginTop: "80px" }}>
          $1500 Total
        </Text>
        {/* <Button label="Reserve">Reserve</Button> */}
      </Group>
    </>
  );
  const image = (
    <Image
      src="http://localhost:4000/assets/images/endroit-secret-mxTJm9ojjPE-unsplash.jpg"
      height={180}
    />
