import React, { useState, useRef } from "react";
import { NumberInput, Group, ActionIcon, Text } from "@mantine/core";
const IncrementDecrementControl = ({ traveler, count, setCount }) => {
  const [value, setValue] = useState(0);
  const operand = useRef();
  const handleChange = (val) => {
    setCount((prevCount) => {
      return { ...prevCount, [traveler.toLowerCase()]: val };
    });
  };

  console.log(traveler.toLowerCase(), count);

  return (
    <Group>
      <div className="increment-traveler-title">
        <Text size="sm" weight={400}>
          {`${traveler}:`}
        </Text>
      </div>
      <ActionIcon
        size={35}
        variant="default"
        onClick={() => operand.current.decrement()}
      >
        -
      </ActionIcon>
      <NumberInput
        hideControls
        value={count}
        onChange={() => setCount(() => handleChange)}
        handlersRef={operand}
        max={8}
        min={0}
        step={1}
        styles={{ input: { width: 47, textAlign: "center" } }}
      />
      <ActionIcon
        size={35}
        variant="default"
        onClick={() => operand.current.increment()}
      >
        +
      </ActionIcon>
    </Group>
  );
};

export default IncrementDecrementControl;
