import React, { useState } from "react";
import { Button, Segment } from "semantic-ui-react";
import { useForm } from "@mantine/hooks";
import { TextInput, PasswordInput } from "@mantine/core";
import PasswordPopOver from "./PasswordPopOver";

const LoginForm = ({ setUser }) => {
  //
  const form = useForm({
    initialValues: {
      username: "",
      password: "",
    },
  });

  const handleSubmit = form.onSubmit((values) => {
    fetch("http://localhost:3000/login", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(values),
    }).then((res) => {
      if (res.ok) {
        res.json().then((data) => {
          console.log(data);
        });
      } else {
        res.json().then((data) => {
          console.log(data.errors);
        });
      }
    });
  });

  return (
    <div>
      <form onSubmit={handleSubmit}>
        <TextInput
          placeholder="Username"
          label="Username"
          // required
          {...form.getInputProps("username")}
        />
        <PasswordPopOver
          formPassword={form.values.password}
          target={
            <PasswordInput
              placeholder="Password"
              label="Password"
              // required
              {...form.getInputProps("password")}
              description="Password should include letters in lower and uppercase, at least 1 number, at least 1 special symbol"
            />
          }
        />
        <br></br>
        {/* <PhoneInput placeholder="Enter phone number" required /> */}
        <Segment inverted>
          <Button type="submit" basic inverted color="violet">
            Submit
          </Button>
        </Segment>
      </form>
    </div>
  );
};

export default LoginForm;
