import React, { useState } from "react";
import {
  Button,
  Container,
  Input,
  Grid,
  Text,
  Popover,
  TextInput,
} from "@mantine/core";
import { IconBed, IconCalendar, IconUsers } from "@tabler/icons";
import IncrementDecrementControl from "./IncrementDecrementControl";
import { DateRange } from "react-date-range";
import { format, addDays } from "date-fns";

import "react-date-range/dist/styles.css"; // main style file
import "react-date-range/dist/theme/default.css"; // theme css file
const HotelSearchBar = () => {
  const [openedPopover, setOpenedPopover] = useState(false);
  const [openedCal, setOpenedCal] = useState(false);
  const [range, setRange] = useState([
    {
      startDate: new Date(),
      endDate: addDays(new Date(), 7),
      key: "selection",
    },
  ]);
  const [guestCount, setGuestCount] = useState({
    adults: 1,
    children: 0,
  });
  // console.log(date[0].startDate);
  const handleChange = (value) => {
    // setDateRange([
    //   {
    //     startDate: value.range1.startDate,
    //     endDate: value.range1.endDate,
    //   },
    // ]);
    console.table([
      { startDate: value.range1.startDate, endDate: value.range1.endDate },
    ]);
  };
  // console.log(dateRange[0]);
  return (
    <div>
      <Container
      // style={{ backgroundColor: "lightgray" }}
      >
        <Grid>
          <Grid.Col span={3}>
            <Input
              className="hotel-search-input"
              placeholder="Where are you going"
              icon={<IconBed />}
            />
          </Grid.Col>
          <Grid.Col span={3}>
            {/* <RangeCalendar /> */}
            <Input
              icon={<IconCalendar />}
              readOnly
              // disabled
              // placeholder=
              value={`${format(range[0].startDate, "MM/dd/yyyy")} to ${format(
                range[0].endDate,
                "MM/dd/yyyy"
              )}`}
            />
            <span
              onClick={() => setOpenedCal(!openedCal)}
              className="headerSearchText"
            ></span>
            {openedCal && (
              <DateRange
                onChange={(item) => setRange([item.selection])}
                editableDateInputs={true}
                moveRangeOnFirstSelection={false}
                ranges={range}
                // months={1}
                direction="horizontal"
                className="calendarElement"
                minDate={new Date()}
              />
            )}
          </Grid.Col>
          <Grid.Col span={3}>
            <Text onClick={() => setOpenedPopover((o) => !o)}>
              <IconUsers />
              {"Travelers"}
            </Text>
            <Popover
              opened={openedPopover}
              onClose={() => setOpenedPopover(false)}
              position="bottom"
              // placement="end"
              withCloseButton
              title="Edit user"
              transition="pop-top-right"
            >
              <IncrementDecrementControl traveler="Adult(s)" />
              <IncrementDecrementControl traveler="Child" />
            </Popover>
          </Grid.Col>
          <Grid.Col span={3}>
            <span className="search-text"></span>
            <Button>Search</Button>
          </Grid.Col>
        </Grid>
      </Container>
    </div>
  );
};

export default HotelSearchBar;
