import React, { useState } from "react";
import { Button, Container, Input, Grid, Text, Popover } from "@mantine/core";
import { IconBed, IconCalendar, IconUsers } from "@tabler/icons";
import IncrementDecrementControl from "./IncrementDecrementControl";
import { format } from "date-fns";
import { DateRange } from "react-date-range";
import "react-date-range/dist/styles.css"; // main style file
import "react-date-range/dist/theme/default.css"; // theme css file
const HotelSearchBar = () => {
  // const guestCount = [{}];
  const [openedPopover, setOpenedPopover] = useState(false);
  const [openedCal, setOpenedCal] = useState(false);
  const [date, setDate] = useState([
    {
      startDate: new Date(),
      endDate: new Date(),
      selection: "",
    },
  ]);
  console.log(
    `${format(new Date(), "MM/dd/yyyy")} to ${format(new Date(), "MM/dd/yyyy")}`
  );
  return (
    <div>
      <Container style={{ backgroundColor: "lightgray" }}>
        <Grid>
          <Grid.Col span={3}>
            <Input
              className="hotel-search-input"
              placeholder="Where are you going"
              icon={<IconBed />}
            />
          </Grid.Col>
          <Grid.Col span={3}>
            <IconCalendar />
            {/* <RangeCalendar /> */}
            <span className="search-text">
              {" "}
              <DateRange
                editableDateInputs={true}
                onChange={(value) => setDate([value.selection])}
                moveRangeOnFirstSelection={false}
                // ranges={date}
                className="date"
                minDate={new Date()}
              />
            </span>
          </Grid.Col>
          <Grid.Col span={3}>
            <IconUsers onClick={() => setOpenedPopover((o) => !o)} />
            <Text>{"Who's Coming"}</Text>
            <Popover
              opened={openedPopover}
              onClose={() => setOpenedPopover(false)}
              position="bottom"
              // placement="end"
              withCloseButton
              title="Edit user"
              transition="pop-top-right"
            >
              <IncrementDecrementControl />
              <IncrementDecrementControl />
            </Popover>
          </Grid.Col>
          <Grid.Col span={3}>
            <span className="search-text"></span>
            <Button>Search</Button>
          </Grid.Col>
        </Grid>
      </Container>
    </div>
  );
};

export default HotelSearchBar;
