import React from "react";
import { Button, Container, Input, Grid, MultiSelect } from "@mantine/core";
import { IconBed, IconCalendar, IconUsers } from "@tabler/icons";
// import { RangeCalendar } from "@mantine/dates";
const HotelSearchBar = () => {
  const guestCount = [{}];
  return (
    <div>
      {" "}
      <Container style={{ backgroundColor: "lightgray" }}>
        <Grid>
          <Grid.Col span={3}>
            <Input
              className="hotel-search-input"
              placeholder="Where are you going"
              icon={<IconBed />}
            />
          </Grid.Col>
          <Grid.Col span={3}>
            <IconCalendar />
            {/* <RangeCalendar /> */}
            <span className="search-text">Check-in - Check-out</span>
          </Grid.Col>
          <Grid.Col span={3}>
            <MultiSelect
              icon={<IconUsers />}
              data={guestCount}
              defaultValue={["1"]}
            />
            <span className="search-text">Person</span>
          </Grid.Col>
          <Grid.Col span={3}>
            <span className="search-text"></span>
            <Button>Search</Button>
          </Grid.Col>
        </Grid>
      </Container>
    </div>
  );
};

export default HotelSearchBar;
