import React, { useState, useRef } from "react";
import { NumberInput, Group, ActionIcon } from "@mantine/core";
const IncrementDecrementControl = () => {
  const [value, setValue] = useState(0);
  const operand = useRef();
  return (
    <Group>
      <ActionIcon
        size={30}
        variant="default"
        onClick={() => operand.current.decrement()}
      >
        -
      </ActionIcon>
      <NumberInput
        hideControls
        value={value}
        onChange={(val) => setValue(val)}
        handlersRef={operand}
        max={8}
        min={0}
        step={1}
        styles={{ input: { width: 54, textAlign: "center" } }}
      />
      <ActionIcon
        size={42}
        variant="default"
        onClick={() => operand.current.increment()}
      >
        +
      </ActionIcon>
    </Group>
  );
};

export default IncrementDecrementControl;
