import React from "react";
import { useForm } from "@mantine/hooks";
import { TextInput, PasswordInput } from "@mantine/core";
import { Button, Segment } from "semantic-ui-react";

const SignUpForm = () => {
  const form = useForm({
    initialValues: {
      firstName: "",
      lastName: "",
      username: "",
      email: "",
      password: "",
      confirmPassword: "",
    },
  });
  return (
    <div>
      <form>
        <TextInput placeholder="First Name" label="First Name" required />
        <TextInput placeholder="Last Name" label="Last Name" required />
        <TextInput placeholder="Username" label="Username" required />
        <TextInput placeholder="Email" label="Email" required />
        <PasswordInput placeholder="Password" label="Password" required />
        <PasswordInput
          placeholder="Confirm Password"
          label="Confirm Password"
          required
        />
        <Segment>
          <Button type="submit" inverted color="violet">
            Submit
          </Button>
        </Segment>
      </form>
    </div>
  );
};

export default SignUpForm;
