import React from "react";
import { useState } from "react";
import { Modal, Button, Group, Card, Text, Ima } from "@mantine/core";
import LoginForm from "./LoginForm";
import SignUpForm from "./SignUpForm";
const Main = () => {
  const [opened, setOpened] = useState(false);
  const [hasAccount, setHasAccount] = useState(true);

  return (
    <>
      <Card p="xl" style={{ border: "2px solid black" }}>
        {/* top, right, left margins are negative – -1 * theme.spacing.xl */}
        <Group>
          <p>Some Text</p>

          {/* right, left margins are negative – -1 * theme.spacing.xl */}
          <Group position="center">
            <Card.Section>Middle section</Card.Section>
          </Group>

          {/* bottom, right, left margins are negative – -1 * theme.spacing.xl */}

          <Card.Section>Last section</Card.Section>
        </Group>
      </Card>
      <Modal opened={opened} onClose={() => setOpened(false)}>
        {hasAccount ? <LoginForm /> : <SignUpForm />}
        <p
          style={{ textDecoration: "underline" }}
          onClick={() => setHasAccount(() => !hasAccount)}
        >
          {hasAccount ? "Don't have an account?" : "Already have an account?"}
        </p>
      </Modal>
      <Group>
        <Button onClick={() => setOpened(true)}>Login</Button>
      </Group>
    </>
  );
};

export default Main;
