import React, { useState } from "react";
import { Button, Container, Input, Grid, Text, Popover } from "@mantine/core";
import { IconBed, IconCalendar, IconUsers } from "@tabler/icons";
import IncrementDecrementControl from "./IncrementDecrementControl";
// import { RangeCalendar } from "@mantine/dates";
const HotelSearchBar = () => {
  // const guestCount = [{}];
  const [opened, setOpened] = useState(false);
  return (
    <div>
      <Container style={{ backgroundColor: "lightgray" }}>
        <Grid>
          <Grid.Col span={3}>
            <Input
              className="hotel-search-input"
              placeholder="Where are you going"
              icon={<IconBed />}
            />
          </Grid.Col>
          <Grid.Col span={3}>
            <IconCalendar />
            {/* <RangeCalendar /> */}
            <span className="search-text">Check-in - Check-out</span>
          </Grid.Col>
          <Grid.Col span={3}>
            <IconUsers onClick={() => setOpened((o) => !o)} />
            <Text>{"Who's Coming"}</Text>
            <Popover
              opened={!opened}
              onClose={() => setOpened(false)}
              position="bottom"
              // placement="end"
              withCloseButton
              title="Edit user"
              transition="pop-top-right"
            >
              <IncrementDecrementControl />
              <IncrementDecrementControl />
            </Popover>
          </Grid.Col>
          <Grid.Col span={3}>
            <span className="search-text"></span>
            <Button>Search</Button>
          </Grid.Col>
        </Grid>
      </Container>
    </div>
  );
};

export default HotelSearchBar;
