import React, { useState } from "react";
import {
  Button,
  Container,
  Input,
  Grid,
  Text,
  Popover,
  TextInput,
} from "@mantine/core";
import { IconBed, IconCalendar, IconUsers } from "@tabler/icons";
import IncrementDecrementControl from "./IncrementDecrementControl";
import { format } from "date-fns";
import { DateRange } from "react-date-range";
import "react-date-range/dist/styles.css"; // main style file
import "react-date-range/dist/theme/default.css"; // theme css file
const HotelSearchBar = () => {
  // const guestCount = [{}];
  const [openedPopover, setOpenedPopover] = useState(false);
  const [openedCal, setOpenedCal] = useState(false);
  const [date, setDate] = useState([
    {
      startDate: new Date(),
      endDate: null,
      selection: "Check-in - Check-out",
    },
  ]);
  const [guestCount, setGuestCount] = useState({
    adults: 1,
    children: 0,
  });
  console.log(date[0].startDate);
  return (
    <div>
      <Container
      // style={{ backgroundColor: "lightgray" }}
      >
        <Grid>
          <Grid.Col span={3}>
            <Input
              className="hotel-search-input"
              placeholder="Where are you going"
              icon={<IconBed />}
            />
          </Grid.Col>
          <Grid.Col span={3}>
            {/* <RangeCalendar /> */}
            <span
              onClick={() => setOpenedCal(!openedCal)}
              className="headerSearchText"
            >{`${format(date[0].startDate, "MM/dd/yyyy")} to ${format(
              date[0].endDate,
              "MM/dd/yyyy"
            )}`}</span>
            {openedCal && (
              <DateRange
                editableDateInputs={true}
                onChange={(item) => setDate(() => [item.selection])}
                moveRangeOnFirstSelection={false}
                ranges={date}
                className="date"
                minDate={new Date()}
              />
            )}
          </Grid.Col>
          <Grid.Col span={3}>
            <Text onClick={() => setOpenedPopover((o) => !o)}>
              <IconUsers />
              {"Travelers"}
            </Text>
            <Popover
              opened={openedPopover}
              onClose={() => setOpenedPopover(false)}
              position="bottom"
              // placement="end"
              withCloseButton
              title="Edit user"
              transition="pop-top-right"
            >
              <IncrementDecrementControl />
              <IncrementDecrementControl />
            </Popover>
          </Grid.Col>
          <Grid.Col span={3}>
            <span className="search-text"></span>
            <Button>Search</Button>
          </Grid.Col>
        </Grid>
      </Container>
    </div>
  );
};

export default HotelSearchBar;
