import React from "react";
import { useState } from "react";
import { Modal, Button, Group, Card, Text, Image, Grid } from "@mantine/core";
import LoginForm from "./LoginForm";
import SignUpForm from "./SignUpForm";
const Main = () => {
  const [opened, setOpened] = useState(false);
  const [hasAccount, setHasAccount] = useState(true);

  return (
    <>
      <Card style={{ border: "2px solid red", height: "47vh" }}>
        {/* right, left margins are negative â€“ -1 * theme.spacing.xl */}

        <Grid
          style={{
            border: "2px solid yellow",
          }}
        >
          <Grid.Col span={4}>
            <Image style={{ border: "2px solid red" }} />
          </Grid.Col>
        </Grid>
        <Text>Middle section</Text>
        <Image style={{ border: "2px solid red" }} />
      </Card>
      <Modal opened={opened} onClose={() => setOpened(false)}>
        {hasAccount ? <LoginForm /> : <SignUpForm />}
        <p
          style={{ textDecoration: "underline" }}
          onClick={() => setHasAccount(() => !hasAccount)}
        >
          {hasAccount ? "Don't have an account?" : "Already have an account?"}
        </p>
      </Modal>
      <Group>
        <Button onClick={() => setOpened(true)}>Login</Button>
      </Group>
    </>
  );
};

export default Main;
