import React from "react";
import { useState } from "react";
import { Modal, Group, Grid } from "@mantine/core";
import LoginForm from "./LoginForm";
import SignUpForm from "./SignUpForm";
import {
  Card,
  Image,
  View,
  Heading,
  Flex,
  Badge,
  Text,
  Button,
  useTheme,
} from "@aws-amplify/ui-react";
const Main = () => {
  const [opened, setOpened] = useState(false);
  const [hasAccount, setHasAccount] = useState(true);
  const { tokens } = useTheme();

  return (
    <>
      <Modal opened={opened} onClose={() => setOpened(false)}>
        {hasAccount ? <LoginForm /> : <SignUpForm />}
        <p
          style={{ textDecoration: "underline" }}
          onClick={() => setHasAccount(() => !hasAccount)}
        >
          {hasAccount ? "Don't have an account?" : "Already have an account?"}
        </p>
      </Modal>
      <Group>
        <Button onClick={() => setOpened(true)}>Login</Button>
      </Group>
      ////
      <View
        backgroundColor={tokens.colors.background.secondary}
        padding={tokens.space.medium}
      >
        <Card>
          <Flex direction="row" alignItems="flex-start">
            <Image
              alt="Road to milford sound"
              src="/road-to-milford-new-zealand-800w.jpg"
              width="33%"
            />
            <Flex
              direction="column"
              alignItems="flex-start"
              gap={tokens.space.xs}
            >
              <Flex>
                <Badge size="small" variation="info">
                  Plus
                </Badge>
                <Badge size="small" variation="success">
                  Verified
                </Badge>
              </Flex>

              <Heading level={5}>
                New Zealand White Water Outdoor Adventure
              </Heading>

              <Text as="span">
                Join us on this beautiful outdoor adventure through the
                glittering rivers through the snowy peaks on New Zealand.
              </Text>
              <Button variation="primary">Book it</Button>
            </Flex>
          </Flex>
        </Card>
      </View>
    </>
  );
};

export default Main;
