import React from "react";
import { Card } from "primereact/card";
import { Button, Group, Grid, Image, Text } from "@mantine/core";
import "primeicons/primeicons.css";
import "primereact/resources/themes/lara-light-indigo/theme.css";
import "primereact/resources/primereact.css";

const HotelRoomCard = () => {
  const footer = (
    <>
      <Group position="left">
        <Text weight={500}>$300</Text>
      </Group>
      <Text weight={200} size="sm">
        Total
      </Text>
      <Group position="right">
        <Button label="Reserve">Reserve</Button>
      </Group>
    </>
  );
  const image = (
    <Image
      src="http://localhost:4000/assets/images/endroit-secret-mxTJm9ojjPE-unsplash.jpg"
      height={180}
    />
  );
  return (
    <Grid.Col span={3}>
      <Card
        style={{ width: "25em", padding: "1em", marginLeft: "40px" }}
        title={"Hotel Room Name Goes Here"}
        header={image}
        footer={footer}
      >
        <Text>amenities go here</Text>
