import React, { useState } from "react";
import { useForm } from "@mantine/hooks";
import { TextInput, PasswordInput, Popover } from "@mantine/core";
import { Button, Segment } from "semantic-ui-react";
import PasswordPopOver from "./PasswordPopOver";

const SignUpForm = ({ setUser, getCurrentUser }) => {
  const [errors, setErrors] = useState({});
  const form = useForm({
    initialValues: {
      username: "",
      email: "",
      password: "",
    },
  });

  const handleSubmit = form.onSubmit(async (values) => {
    let res = await fetch("http://127.0.0.1:3000/signup", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(values),
    });
    if (res.ok) {
      let data = await res.json();
      setUser(data);
      getCurrentUser();
    } else {
      let { errors } = await res.json();
      setErrors(errors);
    }
    form.reset();
  });

  return (
    <div>
      <form onSubmit={handleSubmit}>
        <TextInput
          placeholder="Username"
          label="Username"
          required
          {...form.getInputProps("username")}
        />
        <TextInput
          placeholder="Email"
          label="Email"
          required
          {...form.getInputProps("email")}
        />
        <PasswordPopOver
          formPassword={form.values.password}
          target={
            <PasswordInput
              placeholder="Password"
              label="Password"
              required
              {...form.getInputProps("password")}
              description="Strong password should include letters in lower and uppercase, at least 1 number, at least 1 special symbol"
            />
          }
        />
        <br></br>
        {/* <PhoneInput placeholder="Enter phone number" required /> */}
        <Segment inverted>
          <Button type="submit" basic inverted color="violet">
            Submit
          </Button>
        </Segment>
      </form>
    </div>
  );
};

export default SignUpForm;
