import { Button, Tabs } from "@mantine/core";
import React from "react";
import { useNavigate } from "react-router-dom";
import ProfileSettings from "./ProfileSettings";
import Reviews from "./ReviewCard";
import { IconMessage, IconSettings, IconLogout } from "@tabler/icons";

const UserProfile = ({ currentUser, setCurrentUser }) => {
  const navigate = useNavigate();

  const handleLogout = () => {
    fetch("logout", {
      method: "DELETE",
    }).then((res) => {
      if (res.ok) {
        setCurrentUser(null);
        // navigate("/");
      }
    });
  };

  return (
    <>
      <Tabs grow>
        <Tabs.Tab
          icon={<IconSettings strokeWidth={1.25} />}
          label="Profile Settings"
        >
          {<ProfileSettings />}
        </Tabs.Tab>
        <Tabs.Tab icon={<IconMessage strokeWidth={1.25} />} label="My Reviews">
          {<Reviews />}
        </Tabs.Tab>

        {/* <Tabs.Tab label="Profile Settings"></Tabs.Tab> */}
        {/* <span>
          <IconLogout strokeWidth={1.25} />
        </span> */}
      </Tabs>
      <Button onClick={handleLogout}>LogOut</Button>
    </>
  );
};

export default UserProfile;
