import React, { useState } from "react";
import { Text } from "@mantine/core";
import { Typography, Rating } from "@mui/material";
import "./HotelCard.css";

const HotelCard = () => {
  const [rating, setRating] = useState(4);
  return (
    <div>
      <div className="list-container">
        <div className="list-item-container">
          <div className="list-item">
            <div className="item-content">
              <div className="item-img-container">
                <div>
                  <img
                    className="item-img"
                    src="https://images.trvl-media.com/hotels/1000000/30000/20600/20571/5ae420b7.jpg?impolicy=resizecrop&rw=455&ra=fit"
                    // alt=""
                  />
                </div>
              </div>
              <div className="item-info">
                <div className="item-title">
                  <Text
                    className="item-title-text"
                    size="lg"
                    // align="left"
                    weight={500}
                    lineClamp={1}
                  >
                    {
                      "Long Ass Hotel Name For No Reason... Idk What To Put Here, Something Longer Than The Title"
                    }
                  </Text>
                </div>
                <div className="item-rating">
                  <Rating
                    name="star-rating"
                    value={4}
                    readOnly
                    disabled
                    size="medium"
                  />
                </div>
                <div className="item-description">
                  <Text
                    className="item-description-text"
                    size="sm"
                    weight={400}
                  >
                    {"Canal Street"}
                  </Text>
                </div>
                <div className="item-price">
                  <Text className="item-price-text" weight={700} size="xl">
                    $450
                  </Text>
                  <Text className="item-total-price" weight={400} size="md">
                    $1350 total
                  </Text>
                </div>
                {/* <div className="item-total-price">
                </div> */}
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default HotelCard;
