import React from "react";
import { useState } from "react";
import { Modal, Group, Grid } from "@mantine/core";
import LoginForm from "./LoginForm";
import SignUpForm from "./SignUpForm";
import {
  Card,
  Image,
  View,
  Heading,
  Flex,
  Badge,
  Text,
  Button,
  useTheme,
} from "@aws-amplify/ui-react";
const Main = () => {
  const [opened, setOpened] = useState(false);
  const [hasAccount, setHasAccount] = useState(true);
  const { tokens } = useTheme();

  return (
    <>
      <Modal opened={opened} onClose={() => setOpened(false)}>
        {hasAccount ? <LoginForm /> : <SignUpForm />}
        <p
          style={{ textDecoration: "underline" }}
          onClick={() => setHasAccount(() => !hasAccount)}
        >
          {hasAccount ? "Don't have an account?" : "Already have an account?"}
        </p>
      </Modal>
      <Group>
        <Button onClick={() => setOpened(true)}>Login</Button>
      </Group>
      ////
      <View
        backgroundColor={tokens.colors.background.secondary}
        padding={tokens.space.medium}
        style={{ width: "50vh", marginLeft: "10vh", border: "1px solid black" }}
      >
        <Card>
          <Flex direction="row" alignItems="flex-start">
            <Image
              alt="Road to milford sound"
              src="https://i.ebayimg.com/images/g/cSsAAOSwb8FiF-48/s-l500.jpg"
              width="200px"
              height="200px"
              border="1px solid black"
            />
            <Flex
              direction="column"
              alignItems="flex-start"
              style={{
                border: "1px solid black",
                // width: "66%",
                padding: "1rem",
              }}
              gap={tokens.space.xs}
            >
              <Heading level={5}>Hotel Name</Heading>
              <Text>Location</Text>
              <Text as="span">Description</Text>
            </Flex>
            <Flex
              direction="column"
              style={{
                border: "1px solid red",
                alignItems: "flex-end",
                // width: "66%",
                // padding: "1rem",
              }}
              gap={tokens.space.xs}
            >
              <Button variation="primary">Book it</Button>
            </Flex>
          </Flex>
        </Card>
      </View>
    </>
  );
};

export default Main;
