import React, { useState } from "react";
import { useForm } from "@mantine/hooks";
import { TextInput, PasswordInput, Popover, Progress } from "@mantine/core";
import { Button, Segment } from "semantic-ui-react";

// import "react-phone-number-input/style.css";
// import PhoneInput from "react-phone-number-input";

const SignUpForm = () => {
  const [errors, setErrors] = useState({});
  const [popoverOpened, setPopoverOpened] = useState(false);
  const form = useForm({
    initialValues: {
      username: "",
      email: "",
      password: "",
      passwordConfrimation: "",
    },
  });

  const handleSubmit = form.onSubmit((values) => {
    console.log(values);
    fetch("http://127.0.0.1:3000/users", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(values),
    }).then((res) => {
      if (res.ok) {
        res.json().then((data) => {
          console.log(data);
        });
      } else {
        res.json().then((data) => {
          setErrors(data.errors);
        });
      }
    });

    form.reset();
  });

  const color = strength === 100 ? "teal" : strength > 50 ? "yellow" : "red";

  console.log("signUpFormErrors", errors);

  return (
    <div>
      <form onSubmit={handleSubmit}>
        <TextInput
          placeholder="Username"
          label="Username"
          // required
          {...form.getInputProps("username")}
        />
        <TextInput
          placeholder="Email"
          label="Email"
          // required
          {...form.getInputProps("email")}
        />

        <Popover
          opened={popoverOpened}
          placement="start"
          position="bottom"
          styles={{ popover: { width: "100%" } }}
          withArrow
          trapFocus={false}
          transition="pop-top-left"
          onFocusCapture={() => setPopoverOpened(true)}
          onBlurCapture={() => setPopoverOpened(false)}
          target={
            <PasswordInput
              placeholder="Password"
              label="Password"
              styles={{ input: { width: "100%" } }}
              // description="Strong password should include letters in lower and uppercase, at least 1 number, at least 1 special symbol"
              required
              {...form.getInputProps("password")}
            />
          }
        >
          <Progress
            color={color}
            // value={strength}
            size={5}
            style={{ marginBottom: 10 }}
          />
        </Popover>

        <PasswordInput
          placeholder="Confirm Password"
          label="Confirm Password"
          // required
          {...form.getInputProps("passwordConfrimation")}
        />
        <br></br>
        {/* <PhoneInput placeholder="Enter phone number" required /> */}
        <Segment>
          <Button type="submit" basic inverted color="violet">
            Submit
          </Button>
        </Segment>
      </form>
    </div>
  );
};

export default SignUpForm;
